import { Field, Form, Formik } from "formik";
import './LandingPage.css';
import avatarImage from '../images/pngtree-man-avatar-image-for-profile-png-image_13001882.png';

interface UserData {
  firstName?: string;
  lastName?: string;
  department?: string;
}

interface ProfileProps {
  avatarImage: string;
  userDatamatch: UserData;
}

const LandingPage = () => {

    const userDatabackend = localStorage.getItem('userData');  
    const userDatamatch = userDatabackend ?  JSON.parse(userDatabackend) : null;


      const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
          const file = event.target.files?.[0];
          if (file) {
              const reader = new FileReader();
              reader.onload = (e) => {
                  // Handle the uploaded image (e.g., update state, upload to server)
                  console.log("Uploaded Image URL: ", e.target?.result); // Log the image URL
              };
              reader.readAsDataURL(file);
          }
      };
    return(
        <>
 <div className="profile-container">
      <div className="profile-content">
        <h2 className="profile-heading">My Profile</h2>
        <div className="profile-card">
          <img src={avatarImage} alt="Profile" className="avatar" />
          <span
                        className="edit-photo-link"
                        onClick={() => document.getElementById('fileInput')?.click()}
                        style={{ cursor: 'pointer', color: 'blue', textDecoration: 'underline' }}
                    >
                        Edit Photo
                    </span>
                    <input 
                        type="file" 
                        id="fileInput" 
                        accept="image/*" 
                        onChange={handleFileUpload} 
                        style={{ display: 'none' }} // Hide the input
                    />
          <p>{userDatamatch?.firstName}</p>
          <p>{userDatamatch?.lastName || 'Patel'}</p>
          <p>{userDatamatch?.department || 'IT'}</p>
          {/* Formik Form for Editing */}
          
        </div>
      </div>
    </div>
        </>
    )
}


export default LandingPage;
