import React, { useState, useEffect } from 'react';
import './LandingPage.css';
import defaultAvatar from '../images/pngtree-man-avatar-image-for-profile-png-image_13001882.png';

interface UserData {
  firstName?: string;
  lastName?: string;
  department?: string;
}

const LandingPage: React.FC = () => {
  const userDatabackend = localStorage.getItem('userData');  
  const userDatamatch: UserData | null = userDatabackend ? JSON.parse(userDatabackend) : null;

  // State to hold the selected avatar image
  const [avatarImage, setAvatarImage] = useState<string>(defaultAvatar);

  // Effect to set the initial avatar image from local storage
  useEffect(() => {
    const storedAvatar = localStorage.getItem('avatarImage');
    if (storedAvatar) {
      setAvatarImage(storedAvatar);
    } else if (userDatamatch?.avatar) {
      setAvatarImage(userDatamatch.avatar); // Fallback to user data avatar
    }
  }, [userDatamatch]);

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        if (e.target?.result) {
          const imageUrl = e.target.result as string; // Cast to string
          setAvatarImage(imageUrl); // Update state with new image URL
          localStorage.setItem('avatarImage', imageUrl); // Save to local storage
        }
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <>
      <div className="profile-container">
        <div className="profile-content">
          <h2 className="profile-heading">My Profile</h2>
          <div className="profile-card">
            <img src={avatarImage} alt="Profile" className="avatar" />
            <span
              className="edit-photo-link"
              onClick={() => document.getElementById('fileInput')?.click()}
              style={{ cursor: 'pointer', color: 'blue', textDecoration: 'underline' }}
            >
              Edit Photo
            </span>
            <input 
              type="file" 
              id="fileInput" 
              accept="image/*" 
              onChange={handleFileUpload} 
              style={{ display: 'none' }} // Hide the input
            />
            <p>{userDatamatch?.firstName || 'First Name'}</p>
            <p>{userDatamatch?.lastName || 'Patel'}</p>
            <p>{userDatamatch?.department || 'IT'}</p>
            {/* Formik Form for Editing */}
            {/* You can add your Formik form here if needed */}
          </div>
        </div>
      </div>
    </>
  );
}

export default LandingPage;
