import React, { useState } from 'react';
import './LandingPage.css';
import defaultAvatar from '../images/pngtree-man-avatar-image-for-profile-png-image_13001882.png';

interface UserData {
  firstName?: string;
  lastName?: string;
  department?: string;
}

const LandingPage: React.FC = () => {
  // Sample user data for demonstration
  const userDatamatch: UserData = {
    firstName: 'John',
    lastName: 'Doe',
    department: 'Engineering',
  };

  // State to hold the selected avatar image
  const [avatarImage, setAvatarImage] = useState<string>(defaultAvatar);

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        if (e.target?.result) {
          const imageUrl = e.target.result as string; // Cast to string
          setAvatarImage(imageUrl); // Update state with new image URL
        }
      };
      reader.readAsDataURL(file); // Read the file as a data URL
    }
  };

  return (
    <>
      <div className="profile-container">
        <div className="profile-content">
          <h2 className="profile-heading">My Profile</h2>
          <div className="card-container">
          <div className="profile-card">
          <div className="avatar-background"></div> {/* Blue arc background */}
            <img src={avatarImage} alt="Profile" className="avatar" />
            <span
              className="edit-photo-link"
              onClick={() => document.getElementById('fileInput')?.click()}
              style={{ cursor: 'pointer', color: 'blue' }}
            >
              Edit Photo
            </span>
            <input 
              type="file" 
              id="fileInput" 
              accept="image/*" 
              onChange={handleFileUpload} 
              style={{ display: 'none' }} // Hide the input
            />
            <div className="profile-details">
            <p>{userDatamatch.firstName || 'First Name'}</p>
            <p>{userDatamatch.lastName || 'Patel'}</p>
            <p>{userDatamatch.department || 'IT'}</p>
            {/* You can add your Formik form here if needed */}
            </div>
          </div>
          <div className="profile-card">
            My Details
          </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default LandingPage;
