import React, { useEffect, useState } from 'react';
import { Formik, FormikHelpers, FormikValues } from 'formik';
import * as Yup from 'yup';
import './Home.css';

const Home = () => {
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isSignUp, setIsSignUp] = useState(false);

  // Fetch the data when the component mounts
  useEffect(() => {
    fetch('http://localhost:5000/api/example') // Replace with your backend URL
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setMessage(data.message); // Store the message from the backend
        setLoading(false); // Turn off the loading state
      })
      .catch((error) => {
        setError(error.message); // Set any error if something goes wrong
        setLoading(false); // Turn off the loading state
      });
  }, []); // Empty array means this will run only once when the component mounts

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className='container'>
      <p>{message}</p> {/* Display the message from the backend */}
      <button className='button'>
      {isSignUp ? 'Already have an account? Sign In' : 'Don’t have an account? Sign Up'}
      </button>
      <div className='form-container'>
        <Formik 
       initialValues={{ email: '', password: '' }}
       validationSchema={Yup.object({
        email: Yup.string().email('Invalid email address').required('Required'),
        password: Yup.string().min(6, 'Must be at least 6 characters').required('Required'),
    })}
    onSubmit={(values) => {
        console.log(values);
        // Add your form submission logic here
      }}
        >
               {({ handleSubmit, handleChange, values, touched, errors }) => (
                <form onSubmit={handleSubmit} className="form-container">
                <div className="form-field">
                  <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    onChange={handleChange}
                    value={values.email}
                    className={touched.email && errors.email ? 'input-error' : ''}
                  />
                  {Formik.errors.email ? (
                    <div className="error">{Formik.errors.email}</div>
                  ) : null}
                </div>
                <div className="form-field">
                  <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    onChange={Formik.handleChange}
                    value={Formik.values.password}
                    className={Formik.errors.password ? 'input-error' : ''}
                  />
                  {Formik.errors.password ? (
                    <div className="error">{Formik.errors.password}</div>
                  ) : null}
                </div>
                <button type="submit" className="button">Sign In</button>
              </form>
           )}
       </Formik>
      </div>
    </div>
  );
};

export default Home;
