import React, { useEffect, useState } from 'react';
import { Formik } from 'formik';
import * as Yup from 'yup';
import './Home.css';
import axios from 'axios';

const Home = () => {

  interface LoginResponse {
    message: string;
  }

  const [isSignUp, setIsSignUp] = useState(false);
  const [data, setData] = useState<LoginResponse | null>(null);
  const [error,setError] =useState('');
  const [email, setEmail]=useState('');
  const [password, setPassword]=useState('');


  const signInValidationForm = Yup.object({
    // email: Yup.string().email('Invalid email address').required('Email is Required'),
    // password: Yup.string().min(6, 'Must be at least 6 characters').required('Password is Required'),
  });
  const signUpValidationForm = Yup.object({
    firstName: Yup.string().required('First Name is required'),
    lastName: Yup.string().required('Last Name is required'),
    email: Yup.string().email('Invalid email address').required('Email is required'),
    phoneNumber: Yup.string().matches(/^[0-9]{10}$/, 'Phone number must be 10 digits').required('Phone number is required'),
    password: Yup.string().min(6, 'Must be at least 6 characters').required('Password is required'),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref('password'), undefined], 'Passwords must match')
      .required('Confirm password is required'),
  });

  const handleSubmit = async()=>{
    try {
          const response = await axios.post('http://localhost:5000/api/auth/login', {
            email,
            password,
          });
          console.log(response,"response");
          if(response.data.data.email == email && response.data.data.password == password){
            setData(response.data.data);
            console.log(response.data,"final data");
          }
        }
    catch(err:any){
        setError(err.message);
    }
  }
     
  const toggleForm = () => {
    setIsSignUp((prev) => !prev);
  };

  useEffect(()=>{
    // handleSubmit();
  },[])

  return (
    <div className='container'>
      <button className='button' onClick={toggleForm}>
      {isSignUp ? 'Already have an account? Sign In' : 'Don’t have an account? Sign Up'}
      </button>
      <div className='form-container'>
        <Formik 
      initialValues={ isSignUp ? { firstName: '', lastName: '', email: '', phoneNumber: '', password: '', confirmPassword: '' }  : { email: '', password: '' }}
      validationSchema={ isSignUp ? signUpValidationForm :signInValidationForm }
      onSubmit={handleSubmit}
        >
               {({ handleSubmit, handleChange, values, touched, errors }) => (
                <form onSubmit={handleSubmit} className='form'>
                  {isSignUp ? 
                  <>
                    <div className='form-field'>
                      <label htmlFor='firstName'>First Name</label>
                      <input
                        type='text'
                        id='firstName'
                        name='firstName'
                        onChange={handleChange}
                        value={values.firstName}
                        className={touched.firstName && errors.firstName ? 'input-error' : ''}
                      />
                      {touched.firstName && errors.firstName ? <div className='error'>{errors.firstName}</div> : null}
                    </div>
                    <div className='form-field'>
                      <label htmlFor='lastName'>Last Name</label>
                      <input
                        type='text'
                        id='lastName'
                        name='lastName'
                        onChange={handleChange}
                        value={values.lastName}
                        className={touched.lastName && errors.lastName ? 'input-error' : ''}
                      />
                      {touched.lastName && errors.lastName ? <div className='error'>{errors.lastName}</div> : null}
                    </div>
                    <div className='form-field'>
                      <label htmlFor='phoneNumber'>Phone Number</label>
                      <input
                        type='text'
                        id='phoneNumber'
                        name='phoneNumber'
                        onChange={handleChange}
                        value={values.phoneNumber}
                        className={touched.phoneNumber && errors.phoneNumber ? 'input-error' : ''}
                      />
                      {touched.firstName && errors.firstName ? <div className='error'>{errors.firstName}</div> : null}
                    </div>
                    <div className='form-field'>
                      <label htmlFor='email'>Email</label>
                      <input
                        type='email'
                        id='email'
                        name='email'
                        onChange={handleChange}
                        value={values.email}
                        className={touched.email && errors.email ? 'input-error' : ''}
                      />
                      {touched.email && errors.email ? <div className='error'>{errors.email}</div> : null}
                    </div>
                    <div className='form-field'>
                      <label htmlFor='password'>Password</label>
                        <input
                          type='password'
                          id='password'
                          name='password'
                          onChange={handleChange}
                          value={values.password}
                          className={touched.password && errors.password ? 'input-error' : ''}
                        />
                        {touched.password && errors.password ? <div className='error'>{errors.password}</div> : null}
                    </div>
                    <div className='form-field'>
                      <label htmlFor='firstName'>Confirm Password</label>
                      <input
                        type='password'
                        id='confirmPassword'
                        name='confirmPassword'
                        onChange={handleChange}
                        value={values.confirmPassword}
                        className={touched.confirmPassword && errors.confirmPassword ? 'input-error' : ''}
                      />
                      {touched.confirmPassword && errors.confirmPassword ? <div className='error'>{errors.confirmPassword}</div> : null}
                    </div>
                 </>
                  :
                  <>
                    <div className='form-field'>
                  <label htmlFor='email'>Email  </label>
                  <input 
                  type='email'
                  id='email'
                  name='email'
                  onChange={(e:any)=>setEmail(e.target.value)}
                  value={email}
                  className={touched.email && errors.email ? 'input-error' : ''}>
                  </input>
                {touched.email && errors.email ? <div className='error'>{errors.email}</div> : null}
              </div>

              <div className="form-field">
                  <label htmlFor="password">Password</label>
                  <input
                      type="password"
                      id="password"
                      name="password"
                      onChange={(e:any)=>setPassword(e.target.value)}
                      value={password}
                      className={touched.password && errors.password ? 'input-error' : ''}
                  />
                  {touched.password && errors.password ? <div className="error">{errors.password}</div> : null}
              </div>
             
              </>
                  }
                    <button type="submit" className="button">{isSignUp ? 'Sign Up' : 'Sign In'}</button>
                </form>
           )}
       </Formik>
      </div>
    </div>

  );
};

export default Home;
