import React, { useEffect, useState } from 'react';
import { Formik, FormikHelpers, FormikValues } from 'formik';
import * as Yup from 'yup';
import './Home.css';

const Home = () => {
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isSignUp, setIsSignUp] = useState(false);

  const signInValidationForm = Yup.object({
    email: Yup.string().email('Invalid email address').required('Email is Required'),
    password: Yup.string().min(6, 'Must be at least 6 characters').required('Password is Required'),
  });
  const signUpValidationForm = Yup.object({
    firstName: Yup.string().required('First Name is required'),
    lastName: Yup.string().required('Last Name is required'),
    email: Yup.string().email('Invalid email address').required('Email is required'),
    phoneNumber: Yup.string().matches(/^[0-9]{10}$/, 'Phone number must be 10 digits').required('Phone number is required'),
    password: Yup.string().min(6, 'Must be at least 6 characters').required('Password is required'),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref('password'), null], 'Passwords must match')
      .required('Confirm password is required'),
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  const toggleForm = () => {
    setIsSignUp((prev) => !prev);
  };

    // Fetch the data when the component mounts
    useEffect(() => {
      fetch('http://localhost:5000/api/example') // Replace with your backend URL
        .then((response) => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then((data) => {
          setMessage(data.message); // Store the message from the backend
          setLoading(false); // Turn off the loading state
        })
        .catch((error) => {
          setError(error.message); // Set any error if something goes wrong
          setLoading(false); // Turn off the loading state
        });
    }, []); // Empty array means this will run only once when the component mounts

  return (
    <div className='container'>
      <p>{message}</p> {/* Display the message from the backend */}
      <button className='button' onClick={toggleForm}>
      {isSignUp ? 'Already have an account? Sign In' : 'Don’t have an account? Sign Up'}
      </button>
      <div className='form-container'>
        <Formik 
      initialValues={ isSignUp ? { firstName: '', lastName: '', email: '', phoneNumber: '', password: '', confirmPassword: '' }  : { email: '', password: '' }}
      validationSchema={ isSignUp ? signUpValidationForm :signInValidationForm }
    onSubmit={(values) => {
        console.log(values);
      }}
        >
               {({ handleSubmit, handleChange, values, touched, errors }) => (
                <form onSubmit={handleSubmit} className='form'>
                    <div className='form-field'>
                        <label htmlFor='email'>Email  </label>
                        <input 
                        type='email'
                        id='email'
                        name='email'
                        onChange={handleChange}
                        value={values.email}
                        className={touched.email && errors.email ? 'input-error' : ''}>
                        </input>
                      {touched.email && errors.email ? <div className='error'>{errors.email}</div> : null}
                    </div>

                    <div className="form-field">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            onChange={handleChange}
                            value={values.password}
                            className={touched.password && errors.password ? 'input-error' : ''}
                        />
                        {touched.password && errors.password ? <div className="error">{errors.password}</div> : null}
                    </div>
                    <button type="submit" className="button">{isSignUp ? 'Sign Up' : 'Sign In'}</button>
                </form>
           )}
       </Formik>
      </div>
    </div>

  );
};

export default Home;
